INSERT INTO `wp_pp_login_builder` VALUES (1,'FlatUI Login Theme','<div class=\"login-form\">\r\n\r\n<div class=\"form-group\">\r\n[login-username placeholder=\"username\" class=\"form-control login-field\" id=\"login-name\"]\r\n<label class=\"login-field-icon fui-user\" for=\"login-name\"></label>\r\n</div>\r\n\r\n<div class=\"form-group\">\r\n[login-password placeholder=\"password\" class=\"form-control login-field\" id=\"login-pass\"]\r\n<label class=\"login-field-icon fui-lock\" for=\"login-pass\"></label>\r\n</div>\r\n\r\n<div class=\"form-group\">\r\n[login-remember class=\"flat-checkbox\" id=\"remember-me\"]\r\n<label for=\"remember-me\" class=\"css-label lite-cyan-check\">Remember me</label>\r\n</div>\r\n\r\n[login-submit value=\"Sign In\" class=\"btn btn-primary btn-lg btn-block\"]\r\n\r\n\r\n<div class=\"form-group\"><br/>\r\n[link-registration class=\"reg\" label=\"Register\"] | [link-lost-password class=\"lostp\" label=\"Forgot Password?\"]\r\n</div>\r\n</div>\r\n','/* css class for the login generated errors */\r\n\r\n.profilepress-login-status {\r\n\r\n    background-color: #34495e;\r\n    color: #ffffff;\r\n    border: medium none;\r\n    border-radius: 4px;\r\n    font-size: 15px;\r\n    font-weight: normal;\r\n    line-height: 1.4;\r\n    padding: 8px 5px;\r\n    margin:4px 0;\r\n    transition: border 0.25s linear 0s, color 0.25s linear 0s, background-color 0.25s linear 0s;\r\n}\r\n\r\n.profilepress-login-status a {\r\n  color: #ea9629 !important;\r\n}\r\n\r\n/*\r\nThis login form uses the FlatUI css stylesheet that ships with the plugin hence this look.\r\nIt\'s actually very pretty when implemented.\r\n*/','2018-02-14'),(2,'Fzbuk Login Theme','<div class=\"fzbuk-login-form-wrap\">\r\n\r\n	<h1>Sign In</h1>\r\n\r\n	<div class=\"fzbuk-login-form\">\r\n\r\n		<label>\r\n			[login-username placeholder=\"Username\" id=\"fzbuk-username\"]\r\n		</label>\r\n\r\n		<label>\r\n			[login-password placeholder=\"Password\" id=\"fzbuk-password\"]\r\n		</label>\r\n\r\n		[login-submit value=\"Login\"]\r\n\r\n	</div>\r\n	<h5>[link-lost-password class=\"lostp\" label=\"Forgot Password?\"]</h5>\r\n</div>\r\n','@import url(http://fonts.googleapis.com/css?family=Lato:400,700);\r\n\r\n/* css class for the login generated errors */\r\n.profilepress-login-status {\r\n  -webkit-box-sizing: border-box;\r\n  -moz-box-sizing: border-box;\r\n  box-sizing: border-box;\r\n  color: #fff;\r\n  background: #5170ad;\r\n  background: -moz-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%, #5170ad), color-stop(100%, #355493));\r\n  background: -webkit-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: -o-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: -ms-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: radial-gradient(ellipse at center, #5170ad 0%, #355493 100%);\r\n  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr=\'#5170ad\', endColorstr=\'#355493\',GradientType=1 );\r\n  border: 1px solid #2d416d;\r\n  box-shadow: 0 1px #5670A4 inset, 0 0 10px 5px rgba(0, 0, 0, 0.1);\r\n  border-radius: 5px;\r\n  position: relative;\r\n  width: 360px;\r\n  text-align: center;\r\n  margin: 10px auto;\r\n  padding: 10px;\r\n}\r\n\r\n.profilepress-login-status a {\r\ncolor: #ea9629 !important;\r\nfont-weight:bold;\r\n}\r\n\r\n\r\n\r\n.fzbuk-login-form label {\r\n  display: block;\r\n  margin: 0 !important;\r\n}\r\n\r\n.fzbuk-login-form-wrap {\r\n  -webkit-box-sizing: border-box;\r\n  -moz-box-sizing: border-box;\r\n  box-sizing: border-box;\r\n  background: #5170ad;\r\n  background: -moz-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%, #5170ad), color-stop(100%, #355493));\r\n  background: -webkit-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: -o-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: -ms-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: radial-gradient(ellipse at center, #5170ad 0%, #355493 100%);\r\n  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr=\'#5170ad\', endColorstr=\'#355493\',GradientType=1 );\r\n  border: 1px solid #2d416d;\r\n  box-shadow: 0 1px #5670A4 inset, 0 0 10px 5px rgba(0, 0, 0, 0.1);\r\n  border-radius: 5px;\r\n  position: relative;\r\n  width: 360px;\r\n  margin: 10px auto;\r\n  padding: 50px 30px 0 30px;\r\n  text-align: center;\r\n}\r\n.fzbuk-login-form-wrap:before {\r\n  display: block;\r\n  content: \'\';\r\n  width: 58px;\r\n  height: 19px;\r\n  top: 10px;\r\n  left: 10px;\r\n  position: absolute;\r\n}\r\n.fzbuk-login-form-wrap > h1 {\r\n  margin: 0 0 50px 0;\r\n  padding: 0;\r\n  font-size: 26px;\r\n  color: #fff;\r\n}\r\n.fzbuk-login-form-wrap > h5 {\r\n  color: #303030;\r\n  margin-top: 40px;\r\n}\r\n.fzbuk-login-form-wrap > h5 > a {\r\n  font-size: 14px;\r\n  color: #fff;\r\n  text-decoration: none;\r\n  font-weight: 400;\r\n}\r\n\r\n.fzbuk-login-form input[type=\"text\"], .fzbuk-login-form input[type=\"password\"] {\r\n  -webkit-box-sizing: border-box;\r\n  -moz-box-sizing: border-box;\r\n  box-sizing: border-box;\r\n  width: 100% !important;\r\n  border: 1px solid #314d89;\r\n  outline: none !important;\r\n  padding: 12px 20px !important;\r\n  color: #afafaf !important;\r\n  font-weight: 400 !important;\r\n  font-family: \"Lato\", sans-serif !important;\r\n  cursor: text !important;\r\n}\r\n.fzbuk-login-form input[type=\"text\"] {\r\n  border-bottom: none;\r\n  border-radius: 4px 4px 0 0;\r\n  padding-bottom: 13px;\r\n  box-shadow: 0 -1px 0 #E0E0E0 inset, 0 1px 2px rgba(0, 0, 0, 0.23) inset;\r\n}\r\n.fzbuk-login-form input[type=\"password\"] {\r\n  border-top: none;\r\n  border-radius: 0 0 4px 4px;\r\n  box-shadow: 0 -1px 2px rgba(0, 0, 0, 0.23) inset, 0 1px 2px rgba(255, 255, 255, 0.1);\r\n}\r\n.fzbuk-login-form input[type=\"submit\"] {\r\n  font-family: \"Lato\", sans-serif;\r\n  background: #e0e0e0;\r\n  background: -moz-linear-gradient(top, #e0e0e0 0%, #cecece 100%);\r\n  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #e0e0e0), color-stop(100%, #cecece));\r\n  background: -webkit-linear-gradient(top, #e0e0e0 0%, #cecece 100%);\r\n  background: -o-linear-gradient(top, #e0e0e0 0%, #cecece 100%);\r\n  background: -ms-linear-gradient(top, #e0e0e0 0%, #cecece 100%);\r\n  background: linear-gradient(to bottom, #e0e0e0 0%, #cecece 100%);\r\n  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr=\'#e0e0e0\', endColorstr=\'#cecece\',GradientType=0 );\r\n  display: block;\r\n  margin: 20px auto 0 auto;\r\n  width: 100%;\r\n  border: none;\r\n  border-radius: 3px;\r\n  padding: 8px;\r\n  font-size: 17px;\r\n  color: #636363;\r\n  text-shadow: 0 1px 0 rgba(255, 255, 255, 0.45);\r\n  font-weight: 700;\r\n  box-shadow: 0 1px 3px 1px rgba(0, 0, 0, 0.17), 0 1px 0 rgba(255, 255, 255, 0.36) inset;\r\n}\r\n.fzbuk-login-form input[type=\"submit\"]:hover {\r\n  background: #DDD;\r\n}\r\n.fzbuk-login-form input[type=\"submit\"]:active {\r\n  padding-top: 9px;\r\n  padding-bottom: 7px;\r\n  background: #C9C9C9;\r\n}\r\n\r\n::-webkit-input-placeholder {\r\n    color:    #999;\r\n}\r\n:-moz-placeholder {\r\n    color:    #999;\r\n}\r\n::-moz-placeholder {\r\n    color:    #999;\r\n}\r\n:-ms-input-placeholder {\r\n    color:    #999;\r\n}\r\n','2018-02-14'),(3,'Jakhu Login Theme','<div class=\"jakhu-login-form\">\r\n\r\n	<div class=\"jakhu-header\">\r\n		<h1>Sign In</h1>\r\n		<span>Fill out the form below to login.</span>\r\n	</div>\r\n\r\n\r\n	<div class=\"jakhu-content\">\r\n		[login-username placeholder=\"Username\" class=\"jakhu-input jakhu-username\"]\r\n\r\n		[login-password placeholder=\"Password\" class=\"jakhu-input jakhu-password\"]\r\n	</div>\r\n\r\n	<d